
const generateRandom = (max = 20) => {
    let text = "abcdefghijklmnopqrstuvwxyz1234567890abcdefghijklmnopqrstuvwxyz34567890abcdefghijklmnefghijklmnopqrstz";
    let randomid = "";
    for (let i = 0; i < max; i++) {
        random = (Math.random() * 100).toFixed();
        randomid = randomid + text[random];
    }
    return randomid;
}

const generateOtp = (max = 6, alfanumeric = false) => {
    let text = "12345678901";
    let alfa = "12345678901ABCDEFGHIJKLMNOPQRST12345678901UVWXYZABCDEFGHIJKL12345678901MNOPQRSTUVWXYZABCDEFGHIJNOPQRS";
    let randomid = "";
    if (alfanumeric) {
        for (let i = 0; i < max; i++) {
            random = (Math.random() * 100).toFixed();
            // console.log(random);
            randomid = randomid + alfa[random];
        }
    } else {
        for (let i = 0; i < max; i++) {
            random = (Math.random() * 10).toFixed();
            // console.log(random);
            randomid = randomid + text[random];
        }
    }


    return randomid;
}

module.exports = { generateRandom, generateOtp }